import { useState } from 'react';
import './App.css';

function App() {

  let [good, setGood] = useState([0,0,0]);

  return (
    <div className="App">
        {
          <div>
            good.map(function(a, i){
              <button onClick={ () => {
                let copy = [...good]
                copy[i] = good + 1
                setGood(copy)
              }}>{good[i]}</button>
            })
          </div>
        }
    </div>
  );
}

export default App;
